/* ==UserStyle==
@name         New Dev's Dark StackOverflow (marslo)
@namespace    USO Archive
@version      20210616.12.53
@description  Inherited from New Dev's Dark StackOverflow (https://userstyles.org/styles/170507/new-dev-s-dark-stackoverflow)
@author       marslojiao
@license      CC0-1.0==/UserStyle== */
@-moz-document regexp("https?://.*(stackoverflow|superuser|stackexchange|serverfault|askubuntu).com.*$") {
body {
    background-color: #222 !important;
    color: #DDD;
    background: inherit !important;
}

#hlogo {
    -webkit-filter: invert(100%);
}

#hmenus .nav ul li a {
    background-color: #444;
}

#hmenus .nav ul li.youarehere a,
#hmenus .nav ul li a:hover {
    background-color: #158;
}

#content {
    background-color: #333;
}

.top-bar {
    background-color: #383838;
}

body.question-page.new-topbar {
    color: #EEE;
}

.top-bar .navigation .-item._current .-link {
    color: #FFF;
}

.top-bar .navigation .-link {
    color: #BBB;
}

.top-bar .-logo._glyph:hover {
    background-color: #158;
}

.top-bar .navigation .-link:hover {
    color: #DDD;
    background-color: #158;
}

.top-bar .secondary-nav .-link:hover {
    color: #DDD !important;
    background-color: #158 !important;
}

.top-bar .my-profile:hover {
    background-color: #158;
}

.top-bar .my-profile .-rep {
    color: #EEE;
}

.top-bar .searchbar input[type="text"].f-input {
    color: #AAA;
}

.f-input,
input[type="text"].f-input {
    background-color: #222;
}

#question-header .question-hyperlink {
    color: #F69C55;
}

.vote span,
a {
    color: #1b80cb;
}

a.bounty-link,
.subheader h2 {
    color: #FFF;
}

code *,
pre.default.prettyprint.prettyprinted,
pre.s-code-block,
span.hljs-string {
    background-color: #071726;
		color: #E3E3E3 !important;
}

.s-prose pre:not(.s-code-block),
.s-prose pre:not(.s-code-block) code {
	/* background-color: #1E1E1E !important; */
	background-color: #2E2E2E !important;
	color: #E3E3E3;
}

.comment-text code,
.comment-copy code {
	background-color: #e4e6e8 !important;
	color: #103254 !important;
}

blockquote {
	color: #BBB !important;
}

.post-tag,
.geo-tag,
.container .chosen-choices .search-choice,
.container .chosen-container-multi .chosen-choices li.search-choice {
    background-color: #444;
    color: #8CF;
}

.user-info,
.badgecount,
.post-menu > a {
    color: #AAA;
}

.post-menu > a:hover,
.favoritecount b {
    color: #DDD;
}

.label-key b,
.label-key strong {
    color: #FED;
}

.label-key,
.question-hyperlink:visited,
.answer-hyperlink:visited,
#hot-network-questions ul a:visited {
    color: #DEF;
}

.question-hyperlink,
.answer-hyperlink {
    color: #F69C55;
}

.question-page #answers .answer {
    background-color: #333 !important;
}

.question-page #answers .answer.accepted-answer {
    background-color: #303438 !important;
}

.question-page #answers .answer.downvoted-answer {
    background-color: #383030 !important;
}

.question-page #answers .answer,
tr.comment > td,
.comments,
.subheader,
#question-header {
    border-color: #777;
}

.wmd-preview {
    border-color: #777;
    color: #131313 !important;
}

.user-show-new .user-rep-chart-summary {
    border-color: #777;
}

#profile-summary > .-popout {
    background-color: #222;
    border-color: #777;
}

.welovestackoverflow {
    border-color: #777;
}

.welovestackoverflow a {
    color: #0a5389;
}

.companyPreview > footer a,
.companyPreview > footer a._visited,
.companyPreview > footer a:visited {
    background-color: #222;
}

.user-show-new .user-panel .mini-counts,
.user-show-new #user-tab-answers .answer-votes,
.user-show-new .post-container .vote,
.user-show-new .user-rep .rep-amount .rep-up,
.user-show-new .user-rep .rep-amount .rep-down {
    color: #DDD;
}

#newsletter-ad {
    -webkit-filter: invert(85%);
    color: #000;
}

