.thumbnail-holder[style^="background-image: url('')"], .thumbnail-holder[style^='background-image: url("")'] { background-image: url('http://i.imgur.com/iZrrHLI.png')!important; }
.installed.entry.enabled .thumbnail-holder, #recommended .main-controls .thumbnail-holder.thumbnail { opacity: 0.85!important }
.installed.entry.disabled .thumbnail-holder { opacity: 0.4!important }
.find-styles:hover { opacity: 0.7; }
.find-styles, .thumb_action_link, .thumbnail_install { transition: 0.3s cubic-bezier(0.4, 0, 0.2, 1) }
.bottom_controls { background-color: rgba(71, 71, 71, 0.9)!important }
#header, header { border-bottom: 2px solid #3498db }
#header { padding-bottom: 8px; }
body > div { padding-bottom: 4px }
.tab-header-left { border-right: none; margin-right: -1px!important }
.tabs-container header { background: #3498db }
[data-tooltip]:after { background-color: #0a0a0a }
[data-tooltip]:before { border-top-color: #0a0a0a }
.thumbnail-mask a { font-weight: normal }
.toggle_ball { box-shadow: none }
::-webkit-scrollbar { width: 8px!important }
::-webkit-scrollbar-track { background: transparent !important }
