  div.cke_contents textarea,
  h1#summary-val,
  div#attachmentmodule *,
  input.aui-ss-field,
  div.user-content-block *:not(code):not(pre>code *),
  [class*="dashboard-item"] iframe *,
  [class*="applinks*messages"],
  [class*="jira-activity"] [class*="activity"],
  div[class*="ghx-"],
  [id*="details-module"] *:not([class*="icon"]),
  [class*="s-view-dd-"],
  [class*="aui-dropdown"] strong {
    font-family: var(--font-family-sans) !important;
    font-size: var(--font-size-normal-sans) !important;
  }
  div[class*="ghx-key"] > a, td.issuekey,
  .user-content-block :is(code, code *, pre code),
  .cke_editable :where(code, code *, pre),
  [class*="action-body"] :where(code, code *, pre),
  .mod-content :where(code, code *, pre),
  textarea[class*="cke_source"] {
    font-family: var(--font-family-mono) !important;
    font-size: var(--font-size-normal-mono) !important;
  }
  /* div.field-group input:not([type*="password"], [type*="submit"], [name*="user"], [id*="customfield_"] */
  div.aui-page-header-main > h1#summary-val,
  div.aui-page-header-main > h1#summary-val form#summary-form input.long-field {
    font-size: 26px !important;
  }
  li.ghx-column > h2 {
    font-size: 18px !important;
    align-content: center !important;
  }
  .user-content-block *:not(pre)>code,
  [class*="action-body"] *:not(pre)>code,
  #create-issue-dialog *:not(pre)>code,
  .cke_editable *:not(pre)>code,
  .mod-content *:not(pre)>code {
    color: #d63384 !important;
    font-family: var(--font-family-mono) !important;
    font-size: var(--font-size-normal-mono) !important;
    /* font-style: italic; */
  }
  body.cke_editable :where(p, a, code, pre),
  div.cke_contents textarea {                          /* comments input box */
    font-size: 18px !important;
  }
  /* div.gadget-container > div.dashboard-item-header > h3,                                                         assigned to me      */
  /* .mod-content li.people-details span:where([id*="reporter"], [id*="assignee"]) > span[id*="issue_summary"]      assignee & reporter */
  div.aui-page-header > div.aui-page-header-inner > div.aui-page-header-main > h1, /* system dashboard */
  h1 > span.subnav-container > span#subnav-title > span.subnavigator-title,  /* sprint - xx */
  div#search-header-view > div.header-section-primary > h1.search-title {
    font-family: var(--font-family-handwritten) !important;
  }

  /****************************************************************************************
   * confluence
  body.page-edit :is(h1, h2, h3, h4, h5, li, p, a, span) *,
  [class*="confluence"] *,
  [class*="wysiwyg-macro"] *,
  .wiki-content *,
  table.wysiwyg-fmacro,
  table.wysiwyg-macro+p {
    font-family: var(--font-family-sans) !important;
  }
  ****************************************************************************************/
  input#content-title,
  .wiki-content .contentLayout2,
  span.aui-header-logo-text,
  #autocomplete-dropdown * {
    font-family: var(--font-family-sans) !important;
  }
  body.page-edit code, [class*="confluence"] code *,
  .wysiwyg-macro-body>pre, div.codeContent td.code * {
    font-family: var(--font-family-mono) !important;
  }
  input#content-title {
    font-size: 1.25em !important;
  }
  h1#logo > a > span.aui-header-logo-text {
    font-size: 24px !important;
    line-height: 1;
    padding: 12px 0 0px 0px;
    font-family: var(--font-family-handwritten) !important
  }
  code.bash.comments {
    font-style: italic !important;
  }
  /****************************************************************************************
   * kubernetes.io
   * https://kubernetes.io/docs/tutorials/kubernetes-basics/create-cluster/cluster-interactive/
  ****************************************************************************************/
  #click-to-connect :is(h1,h2),
  section#intro *:not([class*="fa-"]),
  div#course-title *,
  div.step *,
  .settings-dropdown li>a, .webpreview-dropdown li>a {
    font-family: var(--font-family-sans) !important;
  }
  section#terminal.terminal-container *,
  div.xterm-screen canvas,
  canvas.xterm-text-layer .terminal *,
  .terminal-container, .terminal, .terminal[style],
  #xterm-text-layer * {
    font-family: var(--font-family-mono) !important;
    /* font-size: 1.25em !important; */
  }
  section#tutorial :where(code, pre) {
    font-family: var(--font-family-mono) !important;
    font-size: 1.35em !important;
  }
  section#tutorial p,
  div.col-md-12 a.btn,
  #settings-dropdown-terminal a {
    font-size: 16px !important;
  }
  #click-to-connect :is(h1),
  div.row.no-margin h1,
  div.header.banner h1 {
    font-size: 28px !important;
  }
  #click-to-connect :is(h2),
  div#course-title :is(h3),
  div.step :is(h4),
  div#course-details+h1 {
    font-size: 20px !important;
  }
  div#course-details i[class*="fa"],
  .fa, .button, .btn, .section-directions, .fontHeadlineLarge, .file-name-idw {
    font-family: var(--font-family-icon) !important;
  }

  /****************************************************************************************
   * others:
   * .lwptoc_item,                  stackoverflow
   * .book.font-size-2              https://psyteachr.github.io
   * .MuiGrid-container             https://onecompiler.com/
  ****************************************************************************************/
  time, h2[class*="bottom"], .lwptoc_item,
  .book.font-size-2 .book-body .page-inner section,    /* https://psyteachr.github.io */
  .MuiGrid-container h2 {                              /* https://onecompiler.com/ */
    font-size: 14px !important;
  }
  .page-header a>b {                                   /* https://ci.jenkins.io/ */
    font-size: 20px !important;
  }
  [data-target*="readme-toc.content"] *,
  .css-truncate a, .BorderGrid-cell,
  ul.blockListLast > li.blockList,                     /* groovy doc */
  time-ago,
  td.colLast > div.block,                              /* https://javadoc.jenkins.io */
  a.wb-break-all strong,                               /* gist.github.com */
  h4.line * {
    font-family: var(--font-family-sans) !important;
  }
  /* https://www.wikiwand.com/en/Web_colors */
  .mw-parser-output .color-chart-x11-table td:nth-child(2),
  .entry pre>code {
    font-family: var(--font-family-mono) !important;
  }
  /** service now **/
  ul[ng-if*="::user.logged_in"] span:where([class*="navbar-avatar"], [class*="visible-lg-inline"]),
  div[ng-show*="::!isViewNative"] div.logo-name {
    font-family: var(--font-family-handwritten) !important;
    font-size: 1.25em !important;
  }
  /** vault **/
  textarea.input.masked-value {
    font-family: var(--font-family-mono) !important;
  }
  /** zoom.us **/
  h1.r-title * {
    font-size: 22px !important;
  }

  /** csdn.net **/
  .htmledit_views code ol {
    padding-bottom: 5px !important;
  }

  /****************************************************************************************
   * for content: "\f19d";
   * .fa i.fa-clone, i.fa-check                     github pager
   * i.s-fa-external-link, i.s-fa-info-circle       jira
   * i[class*="material-icons-"]                    google search
   * , i[class*="-icon"]
   * .codicon[class*="codicon-"] font: normal normal normal 16px/1 codicon !important;
  ****************************************************************************************/
  ::before, ::after, button, button.btn, span.fa-angle-up * {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    font-family: inherit;
  }

  /**
   * i.fa-clone, i.fa-check,
   * #anchor-navigation-ex-navbar > i[class*='fa'], ⌍
   * #anchorNavigationExGoTop > i[class*='fa'],     ⋮ https://marslo.github.io/ibook
   * a.navigation > i[class*='fa'],                 ⋮
   * a > i[class*='fa'],                            ⌏
   * span > i[class*='fa'],                         https://developer.arm.com/downloads/-/arm-gnu-toolchain-downloads
  **/
  i[class*="fa-"],
  span[class*="glyphicon"],
  span[class*="style_Navbar"], span[class*="avatar"], span[class*="icon-"],
  i[class*="s-fa-"],
  i[class*="material-icons-"],
  i[class*="google-material-icons"],
  i[class*="iconfont"],
  i[class*="ms-Button-icon"], [class*="ms-Button"], .icon-530,
  button[class*="vjs-"], .video-js,
  button[class*="zm-button"], i[class^=zm-icon-], i[class^=zm-dialog],
  [class*="g-button-right"] > [class*="icon-"],
  [class^="u-icon-"],
  .codicon[class*="codicon-"],                   /** gitpod **/
  span[class^="icon"]::before,                   /** https://www4.benefitsolver.com/benefits/BenefitSolverView **/
  .docon                                         /** microsoft doc **/
  {
    font-family: var(--font-family-icon) !important;
  }

  /****************************************************************************************
   * global settings
   *   button#btnSubmit, div.k-widget.k-grid *      projecttracker
   *   div.breadcrumbs *, ul.one-third *,         ⌍
   *   [class*="grepper_answer_button"] *,        ⋮
   *   [class*="answer_title"],                   ⋮
   *   ul.code_language_large *, .a-center        ⌏ https://www.codegrepper.com/
   *   aui-dropdown2                                jira
   *   text-xs, text-sm                             https://app.codecov.io/gh/jenkinsci/code-coverage-api-plugin
  ****************************************************************************************/
  p, h1, h2, h3, h4, h5,
  code :where(h1, h2, h3, h4, h5),
  pre :is(h1, h2, h3, h4, h5),
  button#btnSubmit, div.k-widget.k-grid *,
  div.breadcrumbs *, ul.one-third *,
  ul.code_language_large *, .a-center,
  [class*="grepper_answer_button"] *,
  [class*="answer_title"] {
    font-family: var(--font-family-sans) !important;
  }
  /** .page-header, **/
  h1.page-header :not([class*="hidden-"]),
  h1 > a:not([class*="btn"]),
  h1 > code,
  h1 :not(button):not(button+div):not(a[class*="tab"]):not(a[class*="Tab"]):not(i[class*="fa"]):not([class*="text-xs"]):not([class*="aui-dropdown2"] *),
  h1#gh-pages code,
  .markdown-body h1, .markdown-section h1,
  div[class*="headline"] *:not([class*="btn"]),
  [class*="header"] > h1 *:not([class*="btn"]):not([class*="logo"]),
  div#click-to-connect > div > :not(h1):not(h2),
  div[class*="style_CardRow"] h1 {
    font-size: var(--font-size-h1) !important;
    font-weight: 500 !important;
  }

  /* *:not([class*="confluence"]):not([class*="tablesorter-header-inner"]):not(em) > strong:not([class*="final-path"]), */
  h1:is([class*="text-normal"]):not([id*="summary-val"]),            /* gist.github.com */
  .header h1[data-v-03dd129c],                                       /* ⌍               */
  .app-layout-wrapper .title-wrapper h1[data-v-32d38c0c],            /* ⌏  artifactory  */
  h2[dir*="auto"] > code,                                            /* https://plugins.jenkins.io/kubernetes/ */
  *:not(blockquote):not([class*="-card-"]) >
    h2:not([id*="directions-mode-group"], [dir*="auto"], [class*="panel-title"], [id*="blob-path"], [class*="spanh2"], [class*="fontBodyMedium"], [class*="css-"], [class*="text-sm"], [class*="t-"], [class*="style-name"], [id*="blob-path"], [class*="spanh2"], [id*="directions-mode-group-"]):not([data-v-0b7a2eac]) >
    :is(a:not([class*="btn"], [class*="css-"]),
        span:not([class*="PpN7xc"], [class*="-name"], [class*="btn"], [class*="separator"], [class*="js-path"]),
        *:not(code)
       ),
  h2#gitbook-cli > code, h2#gh-pages code,
  .markdown-body h2:not([class*="btn"]):not([dir*="auto"]),
  .markdown-section h2:not([class*="btn"]):not([dir*="auto"]),
  .markdown-section h2 > mark,
  .markdown-section h2 > a:not([class*="btn"]),
  h2 > code {
    font-size: var(--font-size-h2) !important;
    font-weight: 400 !important;
  }
  h3:not([class*="btn"]):not([class*="ghx-name"]):not([class*="gr-access-section"]):not([class*="f5"]) div:not([class*="react-directory-truncate"]),
  h3 > code, h3#gh-pages code,
  .content h2,                                                       /* https://plugins.jenkins.io/kubernetes/ */
  .markdown-section h2 > code,
  .markdown-body h3 *,
  .markdown-section h3,
  .markdown-section h3 code,
  .markdown-section h3 > mark,
  .markdown-section h3 > a:not([class*="btn"]) {
    font-size: var(--font-size-h3) !important;
    font-weight: 400 !important;
  }
  div[data-v-0b7a2eac] > h2[data-v-0b7a2eac],                        /* artifactory repo name */
  .c-title.t.tts-title a,                                            /* baidu title */
  h4:not([class*="btn"]):not([class*="panel-title"]):not([class*="flex-1"]),
  h4 > code, h4#gh-pages code,
  section[role*="region"] h3,
  .blockList > h3,
  .markdown-section h3 > code,
  .markdown-body h4 *,
  .markdown-section h4 *,
  .markdown-section h4 > mark,
  .markdown-section h4 > a:not([class*="btn"]) {
    font-size: var(--font-size-h4) !important;
    font-weight: 400 !important;
  }

sourceCode:
/* ==UserStyle==
@name           global fonts ( marslo )
@version        2.2.12
@namespace      github.com/marslo
@description    global-wild font customization
@author         marslo.jiao@gmail.com
@homepageURL    https://github.com/marslo/global-fonts
@updateURL      https://github.com/marslo/global-fonts/raw/main/global-fonts.user.css
==/UserStyle== */

@-moz-document regexp("https?://(.*jenkins(?:-staging)?(?:-dev)?(?:-ip-sw-rel)?\\.[^\\.]+\\.com|0.0.0.0|34.233..*.*|3.230.*.*|10.69.*|imarslo.jenkins.com).*") {

  /**
   * regexp("^.*\\-jenkins\\.[^\\.]+\\.com.*$")
   * regexp("^.*jenkins(?:-staging)?\.[^\.]+\.com.*$")
   * regexp("https?://(.*jenkins(?:-staging)?\.[^\.]+\.com|0.0.0.0).*")
   * regexp("https?://(.*jenkins(?:-staging)?(?:-dev)?\.[^\.]+\.com|0.0.0.0).*")
   * regexp("https?://(.*jenkins(?:-staging)?(?:-dev)?\\.[^\\.]+\\.com|0.0.0.0).*")
   * https://www.myfonts.com/pages/whatthefont
   * https://www.whatfontis.com/
   * https://github.com/arrowtype/recursive
  **/

  html {
    --font-family-sans: "Recursive Sans Casual Static Nerd Font", "MFeltPenPRC Nerd Font", "MFeltPenHKSemi Nerd Font", "Grandstander Nerd Font", "Ubuntu", "Candara Nerd Font", "Gisha Nerd Font", "Titillium Nerd Font";
    --font-family-mono: "OperatorMonoLig Nerd Font Mono", "OperatorMono Nerd Font Mono", "JetBrainsMono Nerd Font Mono", "RecMonoCasual Nerd Font Mono", "JetBrainsMono Nerd Font Mono", "Monaco Nerd Font Mono", "Menlo", "Andale Mono", "Ubuntu Mono", "Monofur Nerd Font Mono", "Consolas";
    --font-family-handwritten: "Papyrus Nerd Font", "fantasy";
    --font-family-icon: "Adgs Icons", "Font Awesome 5 Free", "Font Awesome 5 Pro", "Font Awesome 6 Free", "FontAwesome", "Glyphicons Halflings", "Google Material Icons", "Material Icons Extended", "ReadMe-Icons", "StructureFontAwesome", "VideoJS", "codicon", "controlIcons", "iconfont", "icozoom", "retina_icons", "streamline", "union-design-icons", "yunfont", "okticon", "docons", "zipformplus", "Font Awesome 6 Brands", "octicons", "perforceIcons", "FontADP", "Support MDL2 Assets";
    --font-size-normal-sans: 17px;
    --font-size-normal-mono: 18px;
    --font-size-big-mono: 20px;
    --font-size-h1: 34px;
    --font-size-h2: 26px;
    --font-size-h3: 24px;
    --font-size-h4: 20px;
  }

  /** :root cannot take value from var(..) **/
  :root {
    --font-family-sans: "Recursive Sans Casual Static Nerd Font", "MFeltPenHKSemi Nerd Font", "MFeltPenPRC Nerd Font", "Grandstander Nerd Font", "Ubuntu", "Candara Nerd Font", "Gisha Nerd Font", "Titillium Nerd Font" !important;
    --font-family-mono: "OperatorMonoLig Nerd Font Mono", "OperatorMono Nerd Font Mono", "JetBrainsMono Nerd Font Mono", "RecMonoCasual Nerd Font Mono", "JetBrainsMono Nerd Font Mono", "Monaco Nerd Font Mono", "Menlo", "Andale Mono", "Ubuntu Mono", "Monofur Nerd Font Mono", "Consolas" !important;
  }

  *:not(pre):not(code):not(textarea):not(button):not(select):not(option):not(kbd):not(icon):not(input):not(i):not(cursor):not(::before):not(::after):not([role=presentation]):not(pre code):not(pre code *):not(li code):not(li code *):not(code *):not(textarea *):not(* code):not(* pre *):not(* code *):not(div.ace_editor):not([class*="ace_"]):not([class*="ace_"] *):not(canvas.graph-cursor-pointer):not([class*="icon"]):not([class*="fa-"]):not([class*="octicon"]):not([class*="win-icon"]):not([class*="icon-"]):not([class*="ico"]):not([class*="pc"]):not([class*="DPvwYc"]):not([class*="para"]):not([class*="ms-Icon"]):not([class*="sort-caret"]):not([class*="Da"]):not([class*="drop"]):not([class*="bowtie-"]):not([class*="view-"]):not([class*="pl-"]):not([class*=".fa-clone"]):not([class*=".fa-check"]):not([class*="avatar-"]):not([class*='wysiwyg-macro-*']):not([class*="-line"]):not([class*="CodeMirror-"]):not([class*="cm-"]):not([class*="setting-"]):not([class*="gr-"]):not([class*="s-code-"]):not([class*="code"]):not([class*="w3-"]):not([class*="language-"]):not([class*="hljs"]):not([class*="post-"]):not([class*="code"]):not([clas*="line"]):not([class*="js-post"]):not([class*="editor"]):not([class="setting-main"]):not([class*="resizable"]) {
    font-family: var(--font-family-sans) !important;
  }

  /**
   * .systemmessage *, a, body, h1, h2, h3, h4, h5 {
   *   font-family: var(--font-family-sans) !important;
   *   font-size: 100% !important;
   *   background-color: red !important;
   * }
   * :not(div.ace_editor) ,
   * div#main-panel * ,
   * .pane-content                                     build history
   * h1.page-headline, div.cbwf-stage-view *,      ⌝
   * h2.permalinks-header, ul.permalinks-list *    ⌟   job page
   * div.pane-frame *,                                 impact the ace_
   * button,
  **/
  div.tab *,
  [class*="footer"],
  div#view-message *:not(pre):not([class*="cm"]),
  div#side-pannel *,
  span.task-link-text,
  table.middle-align *,
  #breadcrumbs li a,
  #breadcrumbs li a:link ,
  dev.pane.desc *,
  .pane-content a,
  h1.page-headline,
  div.cbwf-stage-view *,
  h2.permalinks-header,
  ul.permalinks-list *,
  table.fileList *,
  td a,
  td *,
  div.signupIntroDefault :is(div, p),
  label.attach-previous.setting-checkbox,
  [class*="jenkins-checkbox"] *,
  div.textarea-preview-container,
  :root:not([class*="ace"]):not(pre>code *):not([class*="jenkins-input"]) {
    font-family: var(--font-family-sans) !important;
  }

  /**
   * login account show
   * https://www.cssfontstack.com/Papyrus
  **/
  div.login a.model-link,
  div#main-panel a[name="skip2content"]+h1,
  ul#breadcrumbs > li.item:first-of-type > a.model-link {
    font-family: var(--font-family-handwritten) !important;
    font-size: 18px !important;
    text-transform: none !important;
  }
  ul#breadcrumbs > li.item:first-of-type > a.model-link,
  div.login a.model-link span.hidden-xs.hidden-sm {
    font-size: 16px !important;
    padding-top: 8px !important;
    font-weight: 550 !important;
  }

  tr.app-summary>td>pre>code>ul>li * {
    font-family: var(--font-family-mono) !important;
    font-size: var(--font-size-normal-mono) !important;
  }
  div#main-panel a[name="skip2content"]+h1 {
    font-size: 28px !important;
  }

  /**
   * tabbar
  **/
  .tabBar .tab a {
    font-size: var(--font-size-normal-sans) !important
  }
  /**
   * setting-* ; div.setting-main *                    configuration
   * textarea.jenkins-input                            snippet output
   * "ace_"                                            Jenkins Pipeline Script Textarea
   * div.setting-main *
   * div.pretty-print                                  config.xml
   *
  **/
  div.setting-main,
  .jenkins-form-label,
  .setting-main > textarea,
  td.setting-main,
  div.pretty-print,
  errorList problem *,
  [data-theme*=material-] #main-panel > pre {
    font-family: var(--font-family-mono) !important;
    font-size: var(--font-size-normal-mono) !important;
  }

  /**.ace-tomorrow {
    background-color: black ;
  }**/

  /**
   * #main-panel>pre, #main-panel>pre *, pre#main-panel, pre#main-panel *, div#main-panel>pre, div#main-panel>pre *, div#main-panel>pre code, div#main-panel>pre code *,
   * :root,
  **/
  ::marker,
  kbd,
  option, option *,
  canvas, canvas *,
  pre, pre *:not([class*="console-output"]),
  pre code, pre code *,
  pre code ul li *,
  pre span, pre span *,
  code, code *,
  input :where(code, pre),
  select, select *,
  script, script *,
  textarea, textarea *,
  #view-message>pre, #view-message>pre *, div#view-message>pre, div#view-message>pre *,
  #side-pannel>pre, #side-pannel>pre *, div#side-pannel>pre, div#side-pannel>pre *,
  .code, .code *,
  div.code *, table.code, table.code *, table.pre, table.pre *,
  .jenkins-input,
  .dropdownList,
  .jenkins-select__input,
  .setting-input,
  select.setting-input, select.setting-input *, select.setting-input.validated,
  input.combobox2.setting-input, input.setting-input,
  textarea.jenkins-input, textarea.setting-input,
  #terminal, #terminal *,
  tr.line_holder *, span[class*=cm-],
  div.help *,
  caption.caption table.diffLineTable *,
  .healthReportDetails td:is([align*="right"]), .coverage-cell-link, .coverage-cell-inner,      /* code coverage tolltip */
  span[class*='pipeline-node']::before {
    font-family: var(--font-family-mono) !important;
    font-size: var(--font-size-normal-mono) !important;
    line-height: 1.5em !important;
  }

  [class*="ace_"],
  [class*="ace_"] *,
  textarea.ace_text-input,
  .ace_active-line,
  cursor,
  [class*="console-output"] *,
  .console-output, .console-output *,
  #main-panel>pre, #main-panel>pre *,
