  .docon                                         /** microsoft doc **/
  {
    font-family: var(--font-family-icon) !important;
  }

  /****************************************************************************************
   * global settings
   *   button#btnSubmit, div.k-widget.k-grid *      projecttracker
   *   div.breadcrumbs *, ul.one-third *,         ⌍
   *   [class*="grepper_answer_button"] *,        ⋮
   *   [class*="answer_title"],                   ⋮
   *   ul.code_language_large *, .a-center        ⌏ https://www.codegrepper.com/
   *   aui-dropdown2                                jira
   *   text-xs, text-sm                             https://app.codecov.io/gh/jenkinsci/code-coverage-api-plugin
  ****************************************************************************************/
  p, h1, h2, h3, h4, h5,
  code :where(h1, h2, h3, h4, h5),
  pre :is(h1, h2, h3, h4, h5),
  button#btnSubmit, div.k-widget.k-grid *,
  div.breadcrumbs *, ul.one-third *,
  ul.code_language_large *, .a-center,
  [class*="grepper_answer_button"] *,
  [class*="answer_title"] {
    font-family: var(--font-family-sans) !important;
  }
  /** .page-header, **/
  h1.page-header :not([class*="hidden-"]),
  h1 > a:not([class*="btn"]),
  h1 > code,
  h1 :not(button):not(button+div):not(a[class*="tab"]):not(a[class*="Tab"]):not(i[class*="fa"]):not([class*="text-xs"]):not([class*="aui-dropdown2"] *),
  h1#gh-pages code,
  .markdown-body h1, .markdown-section h1,
  div[class*="headline"] *:not([class*="btn"]),
  [class*="header"] > h1 *:not([class*="btn"]):not([class*="logo"]),
  div#click-to-connect > div > :not(h1):not(h2),
  div[class*="style_CardRow"] h1 {
    font-size: var(--font-size-h1) !important;
    font-weight: 500 !important;
  }

  /* *:not([class*="confluence"]):not([class*="tablesorter-header-inner"]):not(em) > strong:not([class*="final-path"]), */
  h1:is([class*="text-normal"]):not([id*="summary-val"]),            /* gist.github.com */
  .header h1[data-v-03dd129c],                                       /* ⌍               */
  .app-layout-wrapper .title-wrapper h1[data-v-32d38c0c],            /* ⌏  artifactory  */
  h2[dir*="auto"] > code,                                            /* https://plugins.jenkins.io/kubernetes/ */
  *:not(blockquote):not([class*="-card-"]) >
    h2:not([id*="directions-mode-group"], [dir*="auto"], [class*="panel-title"], [id*="blob-path"], [class*="spanh2"], [class*="fontBodyMedium"], [class*="css-"], [class*="text-sm"], [class*="t-"], [class*="style-name"], [id*="blob-path"], [class*="spanh2"], [id*="directions-mode-group-"]):not([data-v-0b7a2eac]) >
    :is(a:not([class*="btn"], [class*="css-"]),
        span:not([class*="PpN7xc"], [class*="-name"], [class*="btn"], [class*="separator"], [class*="js-path"]),
        *:not(code)
       ),
  h2#gitbook-cli > code, h2#gh-pages code,
  .markdown-body h2:not([class*="btn"]):not([dir*="auto"]),
  .markdown-section h2:not([class*="btn"]):not([dir*="auto"]),
  .markdown-section h2 > mark,
  .markdown-section h2 > a:not([class*="btn"]),
  h2 > code {
    font-size: var(--font-size-h2) !important;
    font-weight: 400 !important;
  }
  h3:not([class*="btn"]):not([class*="ghx-name"]):not([class*="gr-access-section"]):not([class*="f5"]) div:not([class*="react-directory-truncate"]),
  h3 > code, h3#gh-pages code,
  .content h2,                                                       /* https://plugins.jenkins.io/kubernetes/ */
  .markdown-section h2 > code,
  .markdown-body h3 *,
  .markdown-section h3,
  .markdown-section h3 code,
  .markdown-section h3 > mark,
  .markdown-section h3 > a:not([class*="btn"]) {
    font-size: var(--font-size-h3) !important;
    font-weight: 400 !important;
  }
  div[data-v-0b7a2eac] > h2[data-v-0b7a2eac],                        /* artifactory repo name */
  .c-title.t.tts-title a,                                            /* baidu title */
  h4:not([class*="btn"]):not([class*="panel-title"]):not([class*="flex-1"]),
  h4 > code, h4#gh-pages code,
  section[role*="region"] h3,
  .blockList > h3,
  .markdown-section h3 > code,
  .markdown-body h4 *,
  .markdown-section h4 *,
  .markdown-section h4 > mark,
  .markdown-section h4 > a:not([class*="btn"]) {
    font-size: var(--font-size-h4) !important;
    font-weight: 400 !important;
  }

}  /** @-moz-document regexp : for non jenkins hosts **/

