  .kp-blk,
  #fbar,
  .WwiUwd {
    background: var(--bg-secondary);
    border-color: var(--comment);
  }
  .gLFyf,
  .i29hTd .qLLird,
  .sbct:first-child .suggestions-inner-container,
  .RNNXgb {
    color: var(--fg-primary);
  }
  .aajZCb,
  .sbl1,
