    color: var(--red);
  }
  .QCzoEc > svg {
    color: var(--cyan);
  }
  .zgAlFc > span > svg {
    color: var(--orange);
  }
  .Tg7LZd {
    background: var(--bg-primary);
  }
  .k8XOCe,
  .R0xfCb,
  .VCOFK,
  .s8bAkb {
    background: var(--bg-primary);
  }
  .s75CSd,
  .OhScic,
  .AB4Wff {
    color: var(--cyan);
  }
  .sbct:hover {
    border: none !important;
    background: var(--comment);
    cursor: pointer;
  }
  .JCHpcb {
    color: var(--red) !important;
  }
  .wM6W7d,
  .WggQGd {
/** color: var(--magenta) !important; **/
    color: var(--fg-secondary) !important;
    font-family: var(--font-family-sans) !important;
  }
  .minidiv .sfbg {
    background: var(--bg-secondary);
  }
  .g,
  .ssJ7i,
  .iAIpCb,
  .JNkvid .HnYYW,
  .e9EfHf,
  .fm06If .ILfuVd,
  .c2xzTb .ILfuVd,
  .Jb0Zif .c2xzTb .ILfuVd,
  .w5mTAe .ILfuVd,
  .W0urI .ILfuVd,
  .clOx1e,
  .jloFI,
  .HJSKzf,
  .wHYlTd,
  .JgzqYd,
  .YmvwI,
  .FMKtTb,
  .Lu57id,
  .IFnjPb,
  .CSkcDe,
  .related-question-pair div.iDjcJe,
  .X4xFHd,
  .fXNJzc {
    font-family: var(--font-family-sans) !important;
  }
  .wHYlTd,
  .aztjNb {
    line-height: 22px !important;
  }
  #hdtb {
    border: none;
  }
  .GHDvEf,
  .GHDvEf:hover,
  .GHDvEf.selected,
  .GHDvEf.selected:hover {
    background: var(--comment);
  }
  .gb_0b {
    filter: invert(1);
  }
  .vk_bk {
    color: var(--cyan) !important;
  }
  .MjjYud {
    background: var(--bg-primary);
    padding: 4px;
    border: 2px solid var(--comment);
    border-radius: 5px;
    margin-bottom: 15px;
    box-shadow: rgba(0,0,0,0.16) 0px 1px 4px;
    overflow: hidden;
  }
  .NTUU3c {
