/* ==UserStyle==
@name           google gruvbox ( marslo )
@version        1.0.14
@namespace      github.com/marslo
@description    Gruvbox dark theme for google search and translate. inhered from: https://github.com/touhidulshawan/gruvbox-google
@preprocessor   stylus
@author         Touhidul Shawan ( Google Search ); Marslo Jiao ( Google Translate )
@homepageURL    https://github.com/marslo/google-gruvbox
==/UserStyle== */

@-moz-document domain("www.google.com")
  html
    --bg-primary: #282828;
    --bg-secondary: #1d2021;
    --bg-third: #3c3836;
    --comment: #32302f;
    --red: #cc241d;
    --yellow: #d79921;
    --blue: #458588;
    --magenta: #b16286;
    --cyan: #689d6a;
    --orange: #d65d0e;
    --fg-primary: #d5c4a1;
    --fg-secondary: #a89984;
    --font-family-sans: "Recursive Sans Casual Static Nerd Font", "Recursive", "Ubuntu", "Candara", "Gisha", "Titillium", "Google Sans", "Roboto", "Helvetica Neue", "arial", sans-serif;
    --font-family-mono: "OperatorMonoLig Nerd Font Mono", "OperatorMono Nerd Font Mono", "JetBrainsMono Nerd Font Mono", "RecMonoCasual Nerd Font Mono", "Rec Mono Casual", "Monaco", "Comic Mono", "Menlo", "Andale Mono", "Ubuntu Mono", "monofur", "Consolas", "monospace";

  body,
  #hdtbSum,
  #appbar,
  #gb,
  .c93Gbe,
  .s,
  .s .st em,
  .st.s.std em,
  .eJ7tvc,
  .s7d4ef,
  .jloFI,
  .EIJn2,
  .OZ9ddf .Fzsovc,
  #fbar
  #pTwnEc
  .sfbg
    background: var(--bg-primary) !important;
    color: var(--fg-primary) !important;

  code,
  textarea
    font-family: var(--font-family-mono) !important;


  a:link,
  .w,
  #prs a:visited,
  #prs a:active,
  .q:active,
  .q:visited,
  .kl:active,
  .tbotu,
  a.fl:link,
  .fl a,
  .flt,
  a.flt,
  .gl a:link,
  a.mblink,
  .mblink b,
  .i3LlFf,
  .kno-ecr-pt,
  .w8qArf .fl
    color: var(--blue) !important;


  .mblink:visited,
  a:visited
    color: var(--magenta) !important;


  .cv2VAd,
  .kp-blk,
  #fbar,
  .WwiUwd
    background: var(--bg-secondary);
    border-color: var(--comment);

  .gLFyf,
  .i29hTd .qLLird,
  .sbct:first-child .suggestions-inner-container,
  .RNNXgb
    color: var(--fg-primary);


  .aajZCb,
  .sbl1,
  .RNNXgb,
  .sbhl
    background: var(--bg-primary);
    color: var(--fg-primary);
    border: 1px solid var(--cyan);
    border-radius: 0px !important;
    font-family: var(--font-family-mono) !important;

  textarea.gLFyf,
  .YacQv,
  #result-stats
    font-family: var(--font-family-mono) !important;

  .SDkEP
    background: var(--bg-primary) !important;
    color: var(--fg-primary) !important

  .ExCKkf > svg
    color: var(--red);

  .QCzoEc > svg
    color: var(--cyan);

  .zgAlFc > span > svg
    color: var(--orange);

  .Tg7LZd
    background: var(--bg-primary);

  .k8XOCe
  .R0xfCb
  .VCOFK
