@version        1.0.1
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document domain("unix.stackexchange.com") {

.site-header::before {
  z-index: -1;
  content: '';
  position: absolute;
  width: 100%;
  height: 5.1em;
  background-image: radial-gradient(60% 150% at 10% 0, hsla(210,9%,91.5%, 0.4),  hsla(210,9%,91.5%, 0) 68%);
  max-width: unset;
}

