/**
 * regexp("^.*\\-jenkins\\.[^\\.]+\\.com.*$")
 * regexp("^.*jenkins(?:-staging)?\.[^\.]+\.com.*$")
 * regexp("https?://(.*jenkins(?:-staging)?\.[^\.]+\.com|0.0.0.0).*")
**/
*:not(code):not(h1):not(h2):not(pre):not(kbd):not(icon):not(input):not(button):not(i):not([class*="icon"]):not([class*="fa-"]):not([class*="octicon"]):not([class*="win-icon"]):not([class*="icon-"]):not([class*="ace_"]):not([class*="ico"]):not([class*="pc"]):not([class*="DPvwYc"]):not([class*="para"]):not([class*="ms-Icon"]):not([class*="sort-caret"]):not([class*="Da"]):not([class*="drop"]):not([class*="bowtie-"]):not([class*="view-"]):not([class*="pl-"]):not([class*=".fa-clone"]):not([class*="avatar-"]):not([class*='wysiwyg-macro-*']):not([class*="-line"]):not([class*="CodeMirror-"]):not([class*='setting-']):not([class="cm-"]):not(button):not(input):not(select):not(option) {
    font-family: "Titillium", "Gisha", "Candara";
    /*font-size: 16px*/
    font-size: 100%;
  }

  /**
  .systemmessage *, a, body, h1, h2, h3, h4, h5 {
    font-family: "Titillium", "Gisha", "Candara" !important;
    font-size: 100% !important;
    background-color: red !important;
  }
  :not(div.ace_editor),
  div#main-panel * ,
  **/

  /**
  .pane-content                             build history
  h1.page-headline, div.cbwf-stage-view *, h2.permalinks-header, ul.permalinks-list *       job page
  **/
  button, table:not(div.ace_editor), div.tab *,
  div#view-message *, div#side-pannel *,
  span.task-link-text, table.middle-align *,
  #breadcrumbs LI A , #breadcrumbs LI A:link ,
  dev.pane.desc *, .pane-content a, div.pane-frame *,
  h1.page-headline, div.cbwf-stage-view *, h2.permalinks-header, ul.permalinks-list *,
  *:not(code):not(pre):not(kbd):not(textarea):not(input):not(select):not(option),
  table.fileList *, td a, td *,
  label.attach-previous.setting-checkbox {
    font-family: "Titillium", "Gisha", "Candara" !important;
  }

  /**
   *  setting-* ; div.setting-main *                    configuration
  **/

  ::marker, :root, div.ace_editor *,
  textarea, kbd, pre, pre *, pre code, pre code *, pre span, code, code *, input, input *, select, select *, option,
  #main-panel>pre, #main-panel>pre *, pre#main-panel, pre#main-panel *, div#main-panel>pre, div#main-panel>pre *, div#main-panel>pre code, div#main-panel>pre code *,
  #view-message>pre, #view-message>pre *, div#view-message>pre, div#view-message>pre *,
  #side-pannel>pre, #side-pannel>pre *, div#side-pannel>pre, div#side-pannel>pre *,
  div.code *, table.code, table.code *, table.pre, table.pre *,
  select.setting-input, select.setting-input *, select.setting-input.validated,
  input.combobox2.setting-input, input.setting-input, textarea.setting-input,
  pre.console-output *, .console-output, .console-output *, #terminal, #terminal *,
  .lineNum, div.CodeMirror-lines *, tr.line_holder *, .code-line, span[class*=cm-],
  div.setting-main, div.setting-main *, .setting-main > textarea, td.setting-main, div.help *,
  caption.caption,
  div.ace_editor, div.ace_content, table.diffLineTable * {
    font-family: "Monaco", "Menlo", "Andale Mono", "Ubuntu", "Ubuntu Mono", "monofur" !important;
    font-size: 14px !important;
		line-height : 1.5em !important;
  }

  /* buttons in system configuration */
  .yui-button button, button, .url-button {
    display: inline-block !important;
    font-size: 0.75rem !important;
  }

  /* Metrics plugin: Timings table */
  table.bigtable.pane th {
    /* background-color: #999 !important; */
    border: 1px solid !important;
    border-right-color: #4d4c4c !important;
    border-bottom-color: #4d4c4c!important;
    color: #ddd !important;
  }

/**
   * match url:
   * ^((?!job.*configure|.*Monitor/$|.*detail.*pipeline|.*api|.*replay).)*$
   * ^((?!.*Monitor/$|.*detail.*pipeline|.*api|marslo.github.*|shields.io|githubusercontent.com|127.0.0.1|localhost).)*$
   * ^((?!.*\-jenkins\.[^\.]+\.com|.*Monitor/$|.*detail.*pipeline|.*api|marslo.github.*|shields.io|127.0.0.1|localhost).)*$
  **/


  /**
   *    "fa":                                   Gitlab
   *    "octicon":                              Atom & Github
   *    "win-icon":                             Microsoft
   *    "icon-dropdown", "navbar-icon", "icon-home" and "icon-":          Sonar
   *    "ace_":                                 Jenkins
   *    "ico":                                  Sourceforge
   *    "pc" and "DPvwYc" and "Da":             Gmail
   *    "ms-Icon":                              MS OneDrive
   *    "sort-caret":                           Blackduck Hub
   *    "drop", "bowtie-", "view-":             TFS
   *    "pl-":                                  Github
   *    "gr-":																	Gerrit
  **/

  /**
   *  Mac default fonts: , "LucidaGrande", "Kailasa", "Thonburi", "GeezaPro", "KohinoorBangla-Regular"
  **/
  *:not(code):not(h1):not(h2):not(h3):not(h4):not(pre):not(kbd):not(icon):not(input):not(button):not(i):not([class*="icon"]):not([class*="fa-"]):not([class*="octicon"]):not([class*="win-icon"]):not([class*="icon-"]):not([class*="ace_"]):not([class*="ico"]):not([class*="pc"]):not([class*="DPvwYc"]):not([class*="para"]):not([class*="ms-Icon"]):not([class*="sort-caret"]):not([class*="Da"]):not([class*="drop"]):not([class*="bowtie-"]):not([class*="view-"]):not([class*="pl-"]):not([class*=".fa-clone"]):not([class*="avatar-"]):not([class*='wysiwyg-macro-*']):not([class*="-line"]):not([class*="CodeMirror-"]):not([class*="gr-"]) {
    font-family: "Titillium", "Gisha", "Candara";
    /*font-size: 16px*/
    font-size: 100%;
  }

  /**
   *   kbd, pre, code: HTML tag. E.g.: <kbd></kbd>, <pre></pre>
   *   pre *, code *, div.line-numbers, div.code : HTML TAG with Class. E.g.: <div class="code"></div>; <div class="line-numbers"></div>
   *   .line, .line-number, .monospaced: Specified Class Name.
  **/
  span.vsts-build-log,
  /* TFS Log Content */
  div.ace_layer * {
    /* Jenkins Pipeline Script Textarea*/
    font-family: "Monaco", "Menlo", "Ubuntu", "Ubuntu Mono", "Consolas", "Monaco" !important;
  }

  /**
   * .editor,                             Github Codeblock
   * div.ace_content                      Gitlab Diff content ; Jenkins Script Box
   * .solarized-dark,                     http://www.myregexp.com
   * .js-file-line *,                     http://thisdavej.com
   * .changeset-message,                  Jenkins Console Editor
   * .node-log-frame,                     Jenkins Change list
   * #terminal *                          Jenkins Stage View
   * shellcheck
   * .lines-content *                     LeetCode-cn
  **/

  /* Stylish Editor area*/
  textarea, kbd,
  pre, pre code *, pre span, pre.span *,
  code, code *, .code-line, div.code *, table.code,
  input, input *,
  .editor, div.ace_content, .blob-code-addition *, .blob-code-inner *,
  .solarized-dark,
  .EnlighterJS *, .js-file-line *,
  .markdown-body *, .markdown-body pre *,
